const swiper = new Swiper('.swiper-trips', {
  slidesPerView: 4,
  spaceBetween: 30,
  loop: true,
  
  navigation: {
    nextEl: ".swiper-button-next",
    prevEl: ".swiper-button-prev",
  },
});

const swiperTouch = new Swiper('.swiper-trips-touch', {
  slidesPerView: 1,
  spaceBetween: 30,
  loop: true,
  
  navigation: {
    nextEl: ".swiper-button-next",
    prevEl: ".swiper-button-prev",
  },
});

$("a#sideNavToggle").click(function (e) { 
  e.preventDefault();
  $("div#sideNav").removeClass("d-none");
  $("div#sidenavCloser").removeClass("d-none");
});

$("div#sidenavCloser").click(function (e) { 
  e.preventDefault();
  $("div#sideNav").addClass("d-none");
  $("div#sidenavCloser").addClass("d-none");
});

$(document).ready(function () {
  $("div#preloader").fadeOut();
});