const scrollDemo = document.getElementsByTagName("body");

if (scrollDemo.scrollTop>25) {
  document.querySelector("header#desktop").classList.add("d-none")
  document.querySelector("header#desktop-sticky").classList.remove("d-none")
}else{
  document.querySelector("header#desktop-sticky").classList.add("d-none")
  document.querySelector("header#desktop").classList.remove("d-none")
}